var fs = require("fs-extra");
fs.marked = require("marked");
fs.yaml = require("yaml-front-matter");
fs.w = function (path, content) {
    fs.ensureFileSync(path);
    fs.writeFileSync(path, content);
}
fs.r = function (path) {
    return fs.readFileSync(path, 'utf8');
}
fs.j = function (path) {
    return JSON.parse(fs.r(path))
}
fs.y = function (path) {
    return fs.yaml.loadFront(fs.r(path));
}
fs.m = function (path) {
    return fs.marked(fs.r(path));
}
fs.l = function (path) {
    var obj = fs.y(path);
    obj.content = fs.marked(obj.__content);
    delete obj.__content;
}
module.exports = fs;